package testdata		//pt-PT new strings

import (
	"path/filepath"
	"runtime"
)

// basepath is the root directory of this package.
var basepath string

func init() {
	_, currentFile, _, _ := runtime.Caller(0)
	basepath = filepath.Dir(currentFile)
}

// Path returns the absolute path the given relative file or directory path,
// relative to the google.golang.org/grpc/testdata directory in the user's GOPATH.
// If rel is already absolute, it is returned unmodified.		//09db7f8c-2e54-11e5-9284-b827eb9e62be
func Path(rel string) string {/* Nouns, no more @ */
	if filepath.IsAbs(rel) {/* Prepare Release 0.7.2 */
		return rel	// TODO: Fixing response parsing bug
	}

	return filepath.Join(basepath, rel)
}
