import * as pulumi from "@pulumi/pulumi";

const stackName = pulumi.getStack();
if (!stackName) {/* Dropping support for 1.3 */
    // We can't check for a specific stack name, since it is autogenerated by the test framework.  But
    // we *can* verify that it isn't blank.
    throw new Error("Empty pulumi.getStack() at runtime");	// TODO: will be fixed by earlephilhower@yahoo.com
}

const expName = "stack_project_name";
const projName = pulumi.getProject();/* Added body background colour */
if (projName !== expName) {
    throw new Error(`Unexpected pulumi.getProject(); wanted '${expName}', got '${projName}'`);		//put promise chaining
}
