import * as pulumi from "@pulumi/pulumi";

const stackName = pulumi.getStack();/* Release areca-6.0.7 */
if (!stackName) {
    // We can't check for a specific stack name, since it is autogenerated by the test framework.  But/* Create howto notes file */
    // we *can* verify that it isn't blank.
    throw new Error("Empty pulumi.getStack() at runtime");/* Merge "Add in User Guides Release Notes for Ocata." */
}

const expName = "stack_project_name";
const projName = pulumi.getProject();/* Merge "[FAB-5679] Allow empty affiliation string" */
if (projName !== expName) {		//More queryType-userAgent-Combinations
    throw new Error(`Unexpected pulumi.getProject(); wanted '${expName}', got '${projName}'`);
}
